import './App.css';
import {useState} from "react"
import React from "react";


  const root2 = `{
    "type": "dir",
    "children": {
      "home": {
        "type": "dir",
        "children": {
          "myname": {
            "type": "dir",
            "children": {
              "filea/txt": {
                "type": "file"
              },
              "fileb/txt": {
                "type": "file"
              },
              "projects": {
                "type": "dir",
                "children": {
                  "mysupersecretproject": {
                    "type": "dir",
                    "children": {
                      "mysupersecretfile": {
                        "type": "file"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }`

const s = JSON.parse(root2)






function App() {
  const access = (path, object) => {
    return path.split('.').reduce((o, i) => o[i], object)
  }

  const [link, setLink] = useState("children")
  const [linkArray, setLinkArray] = useState(["children"])
  const [isPage, setIsPage] = useState(false)

  const fakeApI = (link) => {
    var myDataresult = access(link,s)
    var myData = Object.keys(myDataresult).map(key => {
      return {type: myDataresult[key].type ,key};
  })
  return myData
  }

  var myData = fakeApI(link)



  const goForward = (type,parent) => {
    console.log(type,parent,"hii")
    if (type == "dir"){
      const newLink = link + "." + parent+".children"
      setLinkArray( [...linkArray,`.${parent}`,".children"]  )
      setLink(newLink)
      setIsPage(false)
    }else{
      console.log("this is file")
      const newLink = link + "." + parent
      setLinkArray( [...linkArray,`.${parent}`]  )
      setLink(newLink)
      setIsPage(true)
    }

  }

  const goBack = (id) => {
    const newArr = linkArray.slice(0,id+2)
    const NewLink = newArr.join("")
    console.log(NewLink)
    console.log(NewLink)
    setLink(NewLink)
    setLinkArray(newArr)
    if(id!=linkArray.length-1) setIsPage(false)
  }

  return (
    <React.Fragment>
      <div>
     { myData.map( (item,idx) => { return <div key={idx} className="{item.type}" onClick={() => goForward(item.type,item.key)   } > {item.type} {item.key} </div>})  }
    </div>
    <div>
    <span onClick = { () => {goBack(-1)}}> root </span>

        { 
          linkArray.map( (item,idx)  => ( 
            (item !==".children" && item !=="children") ?  <span onClick = { ()=> { goBack(idx)} }  key={idx} > {item} </span> : null
          ))    
        }
    </div>


        {isPage  && <div>  this is page { linkArray[linkArray.length-1]}  </div>  }


    <div> 



    </div>

    </React.Fragment>

  );
}

export default App;
