import './App.css';
import {useState} from "react"
import React from "react";
import FakeApI from "./FakeApi";
import BreadCrumb from "./components/BreadCrumb/BreadCrumb";
import {useSelector,useDispatch} from "react-redux";


function App() {
  const dispatch = useDispatch()
  const state = useSelector(state => state)
  const link = state.link
  const linkArray = state.linkArray
  const isPage = state.isPage





  var fa = new FakeApI()
  var myData = fa.fakeApI(link)



  const goForward = (type,parent) => {
    dispatch( {type: "goForward" , payload:{type,parent}})
  }

  const goBack = (id) => {
    dispatch( {type: "goBack" , payload:{id}})
  }

  return (
    <React.Fragment>
      <BreadCrumb className = "breadCrumbs" />
    <div className = "breadCrumbs">
    <span onClick = { () => {goBack(-1)}}> root </span>

        { 
          linkArray.map( (item,idx)  => ( 
            (item !==".children" && item !=="children") ?  <span onClick = { ()=> { goBack(idx)} }  key={idx} > {item} </span> : null
          ))    
        }
    </div>





    <div className="directlyOptions">

     { !isPage ? myData.map( (item,idx) => { return <div key={idx} className="{item.type}" onClick={() => goForward(item.type,item.key)   } > {item.type} {item.key} </div>}): <div>  this is page { linkArray[linkArray.length-1]}  </div>  }
    </div>


    </React.Fragment>

  );
}

export default App;
