import BreadCrumbsItem from "./BreadCrumbsItem/BreadCrumbsItem"


const BreadCrumb = () =>{
    const dispatch = useDispatch()
    const linkArray = useSelector(state => state.linkArray)

    return (
        <div>
            <span onClick = { () => {dispatch(  {type:"goBack", payload:{id:-1}  })} }> root </span>
            { 
            linkArray.map( (item,idx)  => ( 
                (item !==".children" && item !=="children") ?  <BreadCrumbsItem onClick = { ()=> dispatch(  {type:"goBack", payload:{id:idx}  })  }  key={idx} name = {item}/>  : null
            ))    
            }
        </div>
    );
}

export default BreadCrumb
