import './App.css';
import {useState} from "react"
import React from "react";

const  root = `{
  "type": "dir",
  "children": {
  "home": {
  "type": "dir",
  "children": {
  "myname": {
  "type": "dir",
  "children": {
  "filea.txt": {
  "type": "file",
  },
  "fileb.txt": {
  "type": "file",
  },
  "projects": {
  "type": "dir",
  "children": {
  "mysupersecretproject": {
  "type": "dir",
  "children": {
  "mysupersecretfile": {
  "type": "file",
  },
  },
  }
  },
  },
  }
  },
  },
  }
  },
  };`

  const root2 = `{
    "type": "dir",
    "children": {
      "home": {
        "type": "dir",
        "children": {
          "myname": {
            "type": "dir",
            "children": {
              "filea.txt": {
                "type": "file"
              },
              "fileb.txt": {
                "type": "file"
              },
              "projects": {
                "type": "dir",
                "children": {
                  "mysupersecretproject": {
                    "type": "dir",
                    "children": {
                      "mysupersecretfile": {
                        "type": "file"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }`

const s = JSON.parse(root2)


const fakeApI = (link) => {
  const arrr = link
  console.log(arrr)
  var myData = Object.keys(arrr).map(key => {
    return {...arrr[key],key};
})
return myData
}



function App() {
  const arrr = s.children
  const [link, setLink] = useState("children")
  var myData = fakeApI(link)
  console.log(s)

  const access = (path, object) => {
    return path.split('.').reduce((o, i) => o[i], object)
  }

  const goForward = (type,parent) => {
    if (type =="dir"){
      const newLink = link + "." + parent+".children"
      var result = access(newLink,s)
      setLink(result)

    }else{
      console.log("this is file")
    }

  }

  return (
    <React.Fragment>
      <div>
     { myData.map( (item,idx) => { return <div key={idx} className="{item.type}" onClick={() => goForward(item.type,item.key)   } > {item.type} {item.key} </div>})  }
    </div>
    <div>output</div>

    </React.Fragment>

  );
}

export default App;
