import './App.css';
import {useState} from "react"
import React from "react";
import FakeApI from "./FakeApi";
import BreadCrumb from "./components/BreadCrumb/BreadCrumb"



function App() {


  const [link, setLink] = useState("children")
  const [linkArray, setLinkArray] = useState(["children"])
  const [isPage, setIsPage] = useState(false)


  var fa = new FakeApI()
  var myData = fa.fakeApI(link)



  const goForward = (type,parent) => {
    if (type == "dir"){
      const newLink = link + "." + parent+".children"
      setLinkArray( [...linkArray,`.${parent}`,".children"]  )
      setLink(newLink)
      setIsPage(false)
    }else{
      const newLink = link + "." + parent
      setLinkArray( [...linkArray,`.${parent}`]  )
      setLink(newLink)
      setIsPage(true)
    }

  }

  const goBack = (id) => {
    const newArr = linkArray.slice(0,id+2)
    const NewLink = newArr.join("")
    setLink(NewLink)
    setLinkArray(newArr)
    if(id!=linkArray.length-1) setIsPage(false)
  }

  return (
    <React.Fragment>
      <BreadCrumb className = "breadCrumbs" />
    <div className = "breadCrumbs">
    <span onClick = { () => {goBack(-1)}}> root </span>

        { 
          linkArray.map( (item,idx)  => ( 
            (item !==".children" && item !=="children") ?  <span onClick = { ()=> { goBack(idx)} }  key={idx} > {item} </span> : null
          ))    
        }
    </div>





    <div className="directlyOptions">

     { !isPage ? myData.map( (item,idx) => { return <div key={idx} className="{item.type}" onClick={() => goForward(item.type,item.key)   } > {item.type} {item.key} </div>}): <div>  this is page { linkArray[linkArray.length-1]}  </div>  }
    </div>


    </React.Fragment>

  );
}

export default App;
